import axios from "axios";
const API_URL = 'http://localhost:3000/arts';

export const createArt = async (obj) => {
    let result = "";
    try {
        const response = await axios.post(`${API_URL}/`, obj);
        result = response.data;
        console.log(result);
    } catch (error) {
        result = error.response ? error.response.data : error.message; // מידע מדויק יותר בשגיאה
    }
    return result;
};
