import { useState } from "react";
import { useNavigate } from "react-router-dom";
import { userRegister } from "../serivces/userService";

export const Register = () => {
    const [formData, setFormData] = useState({ name: "", mail: "", password: "" });
    const navigate = useNavigate();

    const handleChange = (e) => setFormData({ ...formData, [e.target.name]: e.target.value });

    const handleSubmit = async (e) => {
        e.preventDefault();
        let result = await userRegister(formData);
        let user = { id: result.id, name: result.name, mail: result.mail };
        localStorage.setItem("user", JSON.stringify(user));
        window.dispatchEvent(new Event("storage"));
        navigate('/home');
        window.location.reload();
    };

    return (
        <div className="auth-card login-card">
      <h2 className="auth-title">התחבר</h2>
      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label htmlFor="field1" className="form-label">שם משתמש</label>
          <input
            type="text"
            name="field1"
            value={formData.field1}
            onChange={handleChange}
            className="form-control"
            placeholder="הכנס שם משתמש"
            id="field1"
          />
        </div>
        <div className="mb-3">
          <label htmlFor="field2" className="form-label">סיסמה</label>
          <input
            type="password"
            name="field2"
            value={formData.field2}
            onChange={handleChange}
            className="form-control"
            placeholder="••••••••"
            id="field2"
          />
        </div>
        <button type="submit" className="custom-btn btn-6">התחבר</button>
      </form>
    </div>
    );
};
