import { useState } from "react";
import { userLogin } from "../serivces/userService";
import { useNavigate } from "react-router-dom";

export const Login = () => {
    const [formData, setFormData] = useState({ field1: "", field2: "" });
    const navigate = useNavigate();

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        let result = await userLogin(formData);
        if (result.id) { // ודא שהמשתמש התחבר בהצלחה
            let user = { id: result.id, name: result.name, mail: result.mail };
            localStorage.setItem("user", JSON.stringify(user));
            navigate('/home');
            window.location.reload();
        } else {
            alert("שגיאה בהתחברות. אנא בדוק את שם המשתמש והסיסמה.");
        }
    };

    return (<>
    
        <div className="flex items-center justify-center min-h-screen bg-cover bg-center bg-no-repeat" style={{ backgroundImage: "url('https://source.unsplash.com/1600x900/?art')" }}>
            <div className="bg-white/20 backdrop-blur-lg p-10 rounded-2xl shadow-lg w-96 border border-white/30">
                <h2 className="text-3xl font-extrabold mb-6 text-center text-white tracking-wide">התחבר</h2>
                <form onSubmit={handleSubmit} className="space-y-6">
                    <div>
                        <label className="block mb-2 text-white font-semibold text-sm">שם משתמש</label>
                        <input
                            type="text"
                            name="field1"
                            value={formData.field1}
                            onChange={handleChange}
                            className="w-full p-3 bg-white/30 border border-white/40 rounded-md focus:ring-2 focus:ring-white focus:outline-none placeholder-white/70 text-white"
                            placeholder="הכנס שם משתמש"
                        />
                    </div>
                    <div>
                        <label className="block mb-2 text-white font-semibold text-sm">סיסמה</label>
                        <input
                            type="password"
                            name="field2"
                            value={formData.field2}
                            onChange={handleChange}
                            className="w-full p-3 bg-white/30 border border-white/40 rounded-md focus:ring-2 focus:ring-white focus:outline-none placeholder-white/70 text-white"
                            placeholder="••••••••"
                        />
                    </div>
                    <button type="submit" className="w-full bg-white/20 text-white font-bold p-3 rounded-md hover:bg-white/40 transition duration-300 shadow-md">
                        התחבר
                    </button>
                </form>
                <p className="mt-4 text-center text-white text-sm">
                    אין לך חשבון? <a href="/register" className="text-white font-semibold hover:underline">הרשם כאן</a>
                </p>
            </div>
        </div>
        </>
    );
};
