import { useEffect, useState } from "react"
import { getAllQuotes, putPriority } from "../serivces/quoteService";

export const Quotes = () => {
    const [quotes, setQuotes] = useState([]);
    useEffect(() => {
        const show = async () => {
            let result = await getAllQuotes();
            setQuotes(result);
        }
        show();
    }, [])
    const addPri = (id) => {
        const addPriority = async (id) => {
            let newItem = await putPriority(id);
            setQuotes(quote =>
                quotes.map(item =>
                    item.id === id ? newItem : item
                ));
        }
        addPriority(id)
    }
    return <div>
        {quotes.map((quote, index) => (
            <div key={index}>
                <h2>{quote.title}</h2>
                <p>{quote.text}</p>
                <div style={{ display: "flex", flexDirection: "row", alignItems: "center" }}>
                        <p style={{ marginRight: "10px" }}>{quote.priority}</p>
                        <button 
                            onClick={() => addPri(quote.id)} 
                            style={{
                                background: "none", 
                                border: "none", 
                                fontSize: "20px", 
                                cursor: "pointer", 
                                transition: "color 0.3s",
                                color: quote.priority > 0 ? "red" : "black"
                            }}
                        >
                            {quote.priority > 0 ? "‚ù§Ô∏è" : "ü§ç"}
                        </button>
                    </div>
            </div>
        ))}
    </div>
}
