import React, { useState, useEffect } from 'react';
import { addSugg, getMyQuo, getMySugg, removeQuote, removeSugg } from '../serivces/userService';
import { getAllQuotes } from '../serivces/quoteService';
export const MySuggestions = () => {
    const [userSuggs, setUserSuggs] = useState([]);
    const userId = JSON.parse(localStorage.getItem("user")).id

    useEffect(() => {
        const fetchUserSuggs = async () => {
            const userSuggIds = await getMySugg(userId);
            console.log(userId + "--" + userSuggIds);
            try {
                const allSuggs = await getAllQuotes();
                const filteredSuggs = allSuggs.filter(quote => userSuggIds.includes(quote.id));  // סינון הציטוטים השייכים למשתמש
                setUserSuggs(filteredSuggs);
            } catch (err) {
                console.error("Error fetching user quotes:", err);
            }
        };
        fetchUserSuggs();
    }, []);

    const handleRemoveSugg = async (id) => {
        try {
            await removeSugg(userId, id);  // קריאה לפונקציה להסרת הציטוט
            setUserSuggs(prevQuotes => prevQuotes.filter(quote => quote.id !== id));  // עדכון המערך לאחר ההסרה
        } catch (err) {
            console.error("Failed to remove quote:", err);
        }
    };
    const handleAddSugg = async (sugg) => {
        try {
            navigator()
            let userSuggIds = await addSugg(userId, sugg); 
            const allSuggs = await getAllQuotes();
            const filteredSuggs = allSuggs.filter(quote => userSuggIds.includes(quote.id));  
              setUserSuggs(filteredSuggs);
        } catch (err) {
            console.error("Failed to remove sugg:", err);
        }
    };
    return (
        <div className="sugg-list">
            {userSuggs.length > 0 ? (
                userSuggs.map((quote) => (
                    <div className="sentence-item" key={quote.id}>
                        <span
                            className="trash-button"
                            onClick={() => handleRemoveSugg(quote.id)}
                            style={{ fontSize: '24px', cursor: 'pointer', color: 'red' }}
                        >
                            🗑️
                        </span>
                        <div className="quote-text">
                            <span>{quote.text}</span>
                        </div>
                        <div className="quote-title">
                            <strong>{quote.title}</strong>
                        </div>

                    </div>
                ))
            ) : (
                <p>אין לך ציטוטים עדיין</p>
            )}
            <button onClick={handleAddSugg()}></button>
        </div>
    );

}
