import axios from "axios";
const API_URL = 'http://localhost:3000/users';

export const userLogin = async (user) => {
    let result = "";
    try {
        const response = await axios.post(`${API_URL}/log`, { name: user.field1, pass: user.field2 });
        result = response.data;
    } catch (error) {
        result = error.message;
    }
    return result;
};

export const userRegister = async (user) => {
    let result = "";
    try {
        const response = await axios.post(`${API_URL}/`, user);
        result = response.data;
    } catch (error) {
        result = error.message;
    }
    return result;
};

export const getByIdUser = async (id) => {
    let result;
    try {
        const response = await axios.get(`${API_URL}/${id}`);
        result = response.data;
    } catch (error) {
        result = error.message;
    }
    return result;
};

export const getMySugg = async (id) => {
    let result = "";
    try {
        const response = await getByIdUser(id);
        result = response.suggestions || [];
    } catch (error) {
        result = error.message;
    }
    return result;
};

export const removeSugg = async (id, suggId) => {
    let result = "";
    try {
        const response = await axios.put(`${API_URL}/suggest/d/${id}`, { "quoteId": suggId });
        result = response.data.suggestions || [];
    } catch (error) {
        result = error.message;
    }
    return result;
};

export const addSugg = async (id, sugg) => {
    let result = null;
    try {
        const response = await axios.put(`${API_URL}/suggest/${id}`, {
            "text": sugg.text,
            "title": sugg.title,
            "groupId": sugg.groupId
        });
        result = response.data.suggestions || [];
    } catch (error) {
        result = error.message;
    }
    return result;
};
