import { useState } from "react";
import { userLogin } from "../serivces/userService";
import { useNavigate } from "react-router-dom";
import "./auth.css";

export const Login = () => {
  const [formData, setFormData] = useState({ field1: "", field2: "" });
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    let result = await userLogin(formData);
    let user = { id: result.id, name: result.name, mail: result.mail };
    localStorage.setItem("user", JSON.stringify(user));
    navigate('/home');
    window.location.reload();
  };

  return (
     <div className="auth-card login-card">
      <h2 className="auth-title">התחבר</h2>
      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label htmlFor="field1" className="form-label">שם משתמש</label>
          <input
            type="text"
            name="field1"
            value={formData.field1}
            onChange={handleChange}
            className="form-control"
            placeholder="הכנס שם משתמש"
            id="field1"
          />
        </div>
        <div className="mb-3">
          <label htmlFor="field2" className="form-label">סיסמה</label>
          <input
            type="password"
            name="field2"
            value={formData.field2}
            onChange={handleChange}
            className="form-control"
            placeholder="••••••••"
            id="field2"
          />
        </div>
        <button type="submit" className="custom-btn btn-6">התחבר</button>
      </form>
    </div>
  );
};
