import axios from "axios";
const API_URL = 'http://localhost:3000/arts';

export const createArt = async (obj) => {
    let result = "";
    try {
        const response = await axios.post(`${API_URL}/`, obj);
        result = response.data;
    } catch (error) {
        result = error.response ? error.response.data : error.message; // מידע מדויק יותר בשגיאה
    }
    return result;
};
export const putPriorutyArt = async(id)=>{
    try{
        const response = await axios.put(`${API_URL}/priority/${id}`)
        return response.data
    }
 catch (error) {
    return error.message;
}
}
export const getMyArts = async(id)=>{
    try{
        const response = await axios.get(`${API_URL}/myArts/${id}`)
        return response.data
    }
 catch (error) {
    return error.message;
}
}
export const famous = async(id)=>{
    try{
        const response = await axios.put(`${API_URL}/famous/${id}`)
        return response.data
    }
 catch (error) {
    return error.message;
}
}

export const getFamous = async()=>{
    try{
        const response = await axios.get(API_URL)
        return response.data
    }
 catch (error) {
    return error.message;
}
}
