import { useEffect, useState } from "react";
import { getAllBackgrounds } from "../serivces/backgroundService";
import { addBack, getMyBack, removeBack } from "../serivces/userService";
 // תיקון שגיאת כתיב

export const Backgrounds = () => {
    const [images, setImages] = useState([]);
    const [loadError, setLoadError] = useState(false);
    const [favorites, setFavorites] = useState([]);
    const userId = JSON.parse(localStorage.getItem("user")).id;

    useEffect(() => {
        useEffect(() => {
            const fetchImages = async () => {
                const data = await getAllBeckground();
                const validImages = data.map(img => ({
                    ...img,
                    url: convertDriveUrl(img.url),
                }));
    
                // בדיקה האם המידע השתנה לפני עדכון ה-state
                setImages(prevImages => {
                    const isSame = JSON.stringify(prevImages) === JSON.stringify(validImages);
                    return isSame ? prevImages : validImages;
                });
            }})
            fetchImages()
        // const fetchImages = async () => {
        //     try {
        //         const data = await getAllBackgrounds();
        //         setImages(data);
        //         const userFavorites = await getMyBack(userId);
        //         setFavorites(userFavorites);
        //     } catch (error) {
        //         console.error("Error fetching images:", error);
        //         setLoadError(true);
        //     }
        // };
        
        fetchImages();
    }, [userId]);

    const handleFavoriteClick = async (id) => {
        try {
            if (favorites.includes(id)) {
                await removeBack(userId, id);
            } else {
                await addBack(userId, id);
            }
            const userFavorites = await getMyBack(userId);
            setFavorites(userFavorites);
        } catch (err) {
            console.error("Failed to update favorite:", err);
        }
    };

    const convertDriveUrl = (originalUrl) => {
        const fileIdMatch = originalUrl.match(/[-\w]{25,}/);
        return fileIdMatch ? `https://drive.google.com/thumbnail?id=${fileIdMatch[0]}&sz=w1000` : originalUrl;
    };

    return (
        <div>
            {loadError ? (
                <p>Error loading images.</p>
            ) : images.length > 0 ? (
                images.map((img) => (
                    <div key={img.id} style={{ display: 'inline-block', margin: '10px' }}>
                        <button
                            onClick={() => handleFavoriteClick(img.id)}
                            style={{ color: favorites.includes(img.id) ? 'red' : 'gray' }}
                        >
                            {favorites.includes(img.id) ? 'Unfavorite' : 'Favorite'}
                        </button>
                        <img
                            src={convertDriveUrl(img.url)}
                            alt={`Image ${img.id}`}
                            width="200"
                            onError={(e) => {
                                console.error("Image failed to load:", e.target.src);
                                e.target.src = "path/to/placeholder/image.jpg"; // תמונה חלופית
                            }}
                        />
                    </div>
                ))
            ) : (
                <p>Loading images...</p>
            )}
        </div>
    );
};
