export const Register =()=>{

    
        const [formData, setFormData] = useState({ name: "", mail: "", password: "" });
        const navigate = useNavigate();
    
        const handleChange = (e) => setFormData({ ...formData, [e.target.name]: e.target.value });
    
        const handleSubmit = async (e) => {
            e.preventDefault();
            let result = await userRegister(formData); // קריאה לשרת לרישום
            let user = { id: result.id, name: result.name, mail: result.mail };
            localStorage.setItem("user", JSON.stringify(user));
    
            window.dispatchEvent(new Event("storage")); // עדכון כל הקומפוננטות
            navigate('/home');
        };
    
        return (
            <div className="flex items-center justify-center min-h-screen bg-gray-200">
                <form onSubmit={handleSubmit} className="bg-white p-8 rounded-lg shadow-2xl w-96 border border-gray-300">
                    <h2 className="text-2xl font-bold mb-6 text-center text-gray-700">הרשמה</h2>
                    
                    <div className="mb-5">
                        <label className="block mb-2 text-gray-600 font-medium">שם</label>
                        <input
                            type="text"
                            name="name"
                            value={formData.name}
                            onChange={handleChange}
                            className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-400 outline-none"
                        />
                    </div>
    
                    <div className="mb-5">
                        <label className="block mb-2 text-gray-600 font-medium">אימייל</label>
                        <input
                            type="email"
                            name="mail"
                            value={formData.mail}
                            onChange={handleChange}
                            className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-400 outline-none"
                        />
                    </div>
    
                    <div className="mb-5">
                        <label className="block mb-2 text-gray-600 font-medium">סיסמה</label>
                        <input
                            type="password"
                            name="password"
                            value={formData.password}
                            onChange={handleChange}
                            className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-400 outline-none"
                        />
                    </div>
    
                    <button type="submit" className="w-full bg-blue-500 text-white p-3 rounded-lg hover:bg-blue-600 transition duration-200 shadow-md">
                        הרשמה
                    </button>
                </form>
            </div>
        );
    };
