import { useEffect, useState } from "react"
import { Link, useNavigate } from "react-router-dom"

export const Nav = () => {
    const [name, setName] = useState("");
    const navigate = useNavigate();
    useEffect(() => {
        const updateUser = () => {
            let user = JSON.parse(localStorage.getItem("user"));
            setName(user ? user.name : "");
        };
        updateUser();
        window.addEventListener("storage", updateUser);
        return () => window.removeEventListener("storage", updateUser);
    }, []);
    const logout = () => {
        localStorage.removeItem("user");
        navigate('/home')
        window.location.reload();
    }
    return (
        <header>
            <nav className="navbar navbar-expand-lg navbar-dark bg-dark shadow" >
                <div className="container">
                    <ul className="navbar-nav flex-row d-none d-lg-flex me-auto">
                        {name && <li className="nav-item mx-2"><Link className="nav-link" to={'mySug'}>My Suggestions</Link></li>}
                        {name && <li className="nav-item mx-2"><Link className="nav-link" to={'myQu'}>My Quotes</Link></li>}
                        {name && <li className="nav-item mx-2"><Link className="nav-link" to={'myBack'}>My Backgrounds</Link></li>}
                        {name && <li className="nav-item mx-2"><Link className="nav-link" to={'newArt'}>New Art</Link></li>}
                        <li className="nav-item mx-2"><Link className="nav-link" to={'home'}>Home Page</Link></li>
                        {name && <li className="nav-item mx-2"><Link className="nav-link" to={'quotes'}>Quotes</Link></li>}
                        {name && <li className="nav-item mx-2"><Link className="nav-link" to={'back'}>Backgrounds</Link></li>}
                    </ul>

                    <Link to={'home'} className="navbar-brand mx-auto d-flex align-items-center">
                        <img src="/logo.jpg" alt="Logo" style={{ height: "60px" , borderRadius:"50%"}} />
                    </Link>

                    <ul className="navbar-nav flex-row d-none d-lg-flex ms-auto">
                        {name && <li className="nav-item mx-2"><Link className="nav-link" to={'pro'}>{name}</Link></li>}
                        {!name && <li className="nav-item mx-2"><Link className="nav-link" to={'log'}>Login</Link></li>}
                        {!name && <li className="nav-item mx-2"><Link className="nav-link" to={'reg'}>Register</Link></li>}
                        {name && (<li className="nav-item mx-2"><button className="btn btn-outline-light btn-sm" onClick={logout}>Logout</button></li>)}
                    </ul>

                       <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#mainNav" aria-controls="mainNav" aria-expanded="false" aria-label="Toggle navigation">
                        <span className="navbar-toggler-icon"></span>
                    </button>
                    
                    <div className="collapse navbar-collapse" id="mainNav">
                        <ul className="navbar-nav ms-auto mt-3 mt-lg-0">
                            {name && <li className="nav-item"><Link className="nav-link" to={'pro'}>{name}</Link></li>}
                            {!name && <li className="nav-item"><Link className="nav-link" to={'log'}>Login</Link></li>}
                            {!name && <li className="nav-item"><Link className="nav-link" to={'reg'}>Register</Link></li>}
                            {name && (<li className="nav-item"><button className="btn btn-outline-light btn-sm w-100 mt-2" onClick={logout}>Logout</button></li>)}
                            <hr className="d-lg-none" />
                            {name && <li className="nav-item"><Link className="nav-link" to={'mySug'}>My Suggestions</Link></li>}
                            {name && <li className="nav-item"><Link className="nav-link" to={'myQu'}>My Quotes</Link></li>}
                            {name && <li className="nav-item"><Link className="nav-link" to={'myBack'}>My Backgrounds</Link></li>}
                            {name && <li className="nav-item"><Link className="nav-link" to={'newArt'}>New Art</Link></li>}
                            <li className="nav-item"><Link className="nav-link" to={'home'}>Home Page</Link></li>
                            {name && <li className="nav-item"><Link className="nav-link" to={'quotes'}>Quotes</Link></li>}
                            {name && <li className="nav-item"><Link className="nav-link" to={'back'}>Backgrounds</Link></li>}
                        </ul>
                    </div>
                </div>
            </nav>
        </header>
    )
}
