import { useEffect, useState } from "react";

export const Profile = () => {
    const [user, setUser] = useState(null);
    const [suggestionsCount, setSuggestionsCount] = useState(0);
    const [arts, setArts] = useState([]);
    const [priorityCount, setPriorityCount] = useState(0);

    useEffect(() => {
        const storedUser = JSON.parse(localStorage.getItem("user"));
        if (storedUser) {
            setUser(storedUser);
            fetchUserDetails(storedUser.id);
        }
    }, []);

    const fetchUserDetails = async (userId) => {
        try {
            const response = await fetch(`/api/user/${userId}`);
            const data = await response.json();
            setSuggestionsCount(data.suggestionsCount);
            setArts(data.arts);
            setPriorityCount(data.priorityCount);
        } catch (error) {
            console.error("Error fetching user details:", error);
        }
    };

    if (!user) return <p className="text-center text-gray-500">Loading...</p>;

    return (
        <div className="flex flex-col items-center p-8 bg-gray-100 min-h-screen">
            <div className="bg-white p-6 rounded-lg shadow-lg text-center w-96 border border-gray-300">
                <img 
                    src={user.profileImage || "https://via.placeholder.com/150"} 
                    alt="Profile" 
                    className="w-32 h-32 rounded-full mx-auto mb-4 border border-gray-300"
                />
                <h2 className="text-2xl font-bold text-gray-700">{user.name}</h2>
                <p className="text-gray-600">{user.mail}</p>

                <div className="mt-4">
                    <p className="font-medium text-gray-700">מספר הצעות: {suggestionsCount}</p>
                    <p className="font-medium text-gray-700">Priority על יצירות: {priorityCount}</p>
                </div>
            </div>

            <div className="mt-6 w-full max-w-2xl">
                <h3 className="text-xl font-semibold text-gray-800 mb-4 text-center">היצירות שלי</h3>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    {arts.length > 0 ? (
                        arts.map((art) => (
                            <div key={art.id} className="bg-white p-4 rounded-lg shadow border border-gray-300">
                                <img src={art.image} alt={art.title} className="w-full h-40 object-cover rounded-lg mb-2" />
                                <h4 className="text-lg font-semibold">{art.title}</h4>
                                <p className="text-gray-600">{art.description}</p>
                            </div>
                        ))
                    ) : (
                        <p className="text-center text-gray-500">אין יצירות להצגה</p>
                    )}
                </div>
            </div>
        </div>
    );
};
