import { useEffect, useState, useRef } from "react";
import { getAllBackgrounds } from "../serivces/backgroundService";

export const Backgrounds = () => {
    const [images, setImages] = useState([]);
    const imagesRef = useRef([]);

    useEffect(() => {
        const fetchImages = async () => {
            const data = await getAllBackgrounds();
            setImages(data);
            imagesRef.current = data; // שמירת הנתונים ב-Ref
        };

        fetchImages();
    }, []);

    const convertDriveUrl = (originalUrl) => {
        const fileIdMatch = originalUrl.match(/[-\w]{25,}/);
        return fileIdMatch ? `https://drive.google.com/uc?export=view&id=${fileIdMatch[0]}` : originalUrl;
    };

    return (
        <div>
            {imagesRef.current.length > 0 ? (
                imagesRef.current.map((img, index) => (
                    <img key={index} src={convertDriveUrl(img.url)} alt={`Image ${index}`} width="200" 
                         onError={(e) => console.error("Image failed to load:", e.target.src)} />
                ))
            ) : (
                <p>Loading images...</p>
            )}
        </div>
    );
};
