export const MyQuotes =()=>{

    
        const [userQuotes, setUserQuotes] = useState([]);
        
        useEffect(() => {
            const fetchUserQuotes = async () => {
                const userQuoteIds = await getMyQuo(); 
                console.log(userQuoteIds) // שליפת רשימת ה-IDs של הציטוטים מה-LocalStorage
                if (!userQuoteIds || userQuoteIds.length === 0) {
                    console.log("אין ציטוטים למשתמש.");
                    return;
                }
                try {
                    const allQuotes = await getAll(); 
                    console.log(allQuotes)   
                    const filteredQuotes = allQuotes.filter(quote => userQuoteIds.includes(quote.Id.toString()));  // סינון הציטוטים השייכים למשתמש
                    console.log(filteredQuotes)   
                    setUserQuotes(filteredQuotes);
                } catch (err) {
                    console.error("Error fetching user quotes:", err);
                }
            };
    
            fetchUserQuotes();
        }, []);
    
        const handleRemoveQuote = async (id) => {
            try {
                await removeQuote(id);  // קריאה לפונקציה להסרת הציטוט
                setUserQuotes(prevQuotes => prevQuotes.filter(quote => quote.Id !== id));  // עדכון המערך לאחר ההסרה
            } catch (err) {
                console.error("Failed to remove quote:", err);
            }
        };
    
        return (
            <div className="quote-list">
                {userQuotes.length > 0 ? (
                    userQuotes.map((quote) => (
                        
                        <div className="sentence-item" key={quote.Id}>
                            <span 
                                className="trash-button"
                                onClick={() => handleRemoveQuote(quote.Id)}
                                style={{ fontSize: '24px', cursor: 'pointer', color: 'red' }}
                            >
                                🗑️
                            </span>
                              <span 
                    className="star-button2" 
                    style={{ color: "gold", fontSize: "24px" }}
                >
                    ★
                </span>
                <span className='priority'>{quote.priority}</span>
                            <div className="quote-text">
                                <span>{quote.text}</span>
                            </div>
                            <div className="quote-title">
                                <strong>{quote.title}</strong>
                            </div>
                            
                        </div>
                    ))
                ) : (
                    <p>אין לך ציטוטים עדיין</p>
                )}
            </div>
        );
    
}
