import { useEffect, useState } from "react"
import { Link, useNavigate } from "react-router-dom"

export const Nav = () => {
    const [name, setName] = useState("");
    const navigate = useNavigate();
    useEffect(() => {
        const updateUser = () => {
            let user = JSON.parse(localStorage.getItem("user"));
            setName(user ? user.name : "");
        };
        updateUser();
        window.addEventListener("storage", updateUser);
        return () => window.removeEventListener("storage", updateUser);
    }, []);
    const logout = () => {
        localStorage.removeItem("user");
        navigate('/home')
        window.location.reload();
    }
    return <header >
        <nav className="navbar navbar-inverse" >
            <div className="container-fluid">
                <ul className="navbar-nav me-auto">                      {name && <li className="nav-item"><Link className="nav-link" to={'mySug'}>My Suggestions</Link></li>}
//                         {name && <li className="nav-item"><Link className="nav-link" to={'myQu'}>My Quotes</Link></li>}
//                         {name && <li className="nav-item"><Link className="nav-link" to={'myBack'}>My Backgrounds</Link></li>}
//                         {name && <li className="nav-item"><Link className="nav-link" to={'newArt'}>New Art</Link></li>}
//                         <li className="nav-item"><Link className="nav-link" to={'home'}>Home Page</Link></li>
//                         {name && <li className="nav-item"><Link className="nav-link" to={'quotes'}>Quotes</Link></li>}
//                         {name && <li className="nav-item"><Link className="nav-link" to={'back'}>Backgrounds</Link></li>}
                </ul>

                {/* לוגו במרכז */}
                <Link to={'home'} className="navbar-brand mx-auto">/                         <img src="/logo.jpg" alt="Logo" style={{ height: "50px" }} />
                </Link>

                {/* צד ימין */}
                <ul className="navbar-nav ms-auto">
                    {name && <li className="nav-item"><Link className="nav-link" to={'pro'}>{name}</Link></li>}
                    {!name && <li className="nav-item"><Link className="nav-link" to={'log'}>Login</Link></li>}
                    {!name && <li className="nav-item"><Link className="nav-link" to={'reg'}>Register</Link></li>}
                    {name && (<li className="nav-item"><Link className="nav-link" to="#" onClick={logout}>Logout</Link></li>)}
                </ul>
                {/* <ul className="nav navbar-nav navbar-right"> */}
                {/* {name && <li><Link to={'myArts'} style={{ color: "white" }}>My Arts</Link></li>} */}
                {/* {name && <li><Link to={'mySug'} style={{ color: "white" }}>My Suggestions</Link></li>}
                    {name && <li><Link to={'myQu'} style={{ color: "white" }}>My Quotes</Link></li>}
                    {name && <li><Link to={'myBack'} style={{ color: "white" }}>My Backgrounds</Link></li>}
                    {name && <li><Link to={'newArt'} style={{ color: "white" }}>New Art</Link></li>}
                    <li><Link to={'home'} style={{ color: "white" }}>Home Page</Link></li>
                    {name && <li><Link to={'quotes'} style={{ color: "white" }}>quotes</Link></li>}
                    {name && <li><Link to={'back'} style={{ color: "white" }}>Backgrounds</Link></li>}
                </ul>
                <Link to={'home'} className="navbar-brand mx-auto">
                    <img src="/logo.jpg" alt="Logo" style={{ height: "50px" }} />
                </Link>
                <ul className="nav navbar-nav">
                    <li><Link to={'pro'} style={{ color: "white" }}>{name}</Link></li>
                    {!name && <li><Link to={'log'} style={{ color: "white" }}>Login</Link></li>}
                    {!name && <li><Link to={'reg'} style={{ color: "white" }}>Register</Link></li>}
                    {name && (<li><Link to="#" onClick={logout} style={{ color: "white" }}>Logout</Link></li>)}
                </ul> */}
            </div>
        </nav>
    </header>
}
