import { useState } from "react";
import { userLogin } from "../serivces/userService";
import { useNavigate } from "react-router-dom";

export const Login = () => {
    const [formData, setFormData] = useState({ field1: "", field2: "" });
    const navigate = useNavigate();
    const handleChange = (e) => setFormData({ ...formData, [e.target.name]: e.target.value });

    const handleSubmit = async (e) => {
        e.preventDefault();
        let result = await userLogin(formData);
        // alert(result.mail);
        let user = { id: result.id, name: result.name, mail: result.mail }
        localStorage.setItem("user", JSON.stringify(user))
        navigate('/home');
        window.location.reload();
    };

    return (
        <div className="flex items-center justify-center min-h-screen bg-gradient-to-r from-blue-500 to-purple-600">
        <div className="bg-white p-8 rounded-2xl shadow-2xl w-96 border border-gray-300 transform transition-all duration-500 hover:scale-105">
            <h2 className="text-3xl font-extrabold mb-6 text-center text-gray-800">
                התחבר לחשבון שלך
            </h2>
            <form onSubmit={handleSubmit} className="space-y-5">
                <div>
                    <label className="block mb-2 text-gray-600 font-semibold">אימייל</label>
                    <input
                        type="email"
                        name="email"
                        value={formData.email}
                        onChange={handleChange}
                        className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-purple-400 outline-none transition"
                        placeholder="example@mail.com"
                    />
                </div>
                <div>
                    <label className="block mb-2 text-gray-600 font-semibold">סיסמה</label>
                    <input
                        type="password"
                        name="password"
                        value={formData.password}
                        onChange={handleChange}
                        className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-purple-400 outline-none transition"
                        placeholder="********"
                    />
                </div>
                <button type="submit" className="w-full bg-purple-500 text-white p-3 rounded-lg hover:bg-purple-600 transition duration-300 shadow-md">
                    התחבר
                </button>
            </form>
            <p className="mt-4 text-center text-gray-600 text-sm">
                אין לך חשבון? <a href="/register" className="text-purple-500 font-semibold hover:underline">הרשם כאן</a>
            </p>
        </div>
    </div>
    );
};
