import { useState } from "react";
import { userLogin } from "../serivces/userService";

export const Login = () => {
  const [formData, setFormData] = useState({ field1: "", field2: "" });

  const handleChange = (e) => setFormData({ ...formData, [e.target.name]: e.target.value });

  const handleSubmit = async(e) => {
    e.preventDefault();
    let result = await userLogin(formData);
    // alert(result.mail);
    let user = {id:result.id,name:result.name,mail:result.mail}
    localStorage.setItem("user",Juser)
    
  };

  return (
    <div className="flex items-center justify-center min-h-screen bg-gray-200">
      <form onSubmit={handleSubmit} className="bg-white p-8 rounded-lg shadow-2xl w-96 border border-gray-300">
        <h2 className="text-2xl font-bold mb-6 text-center text-gray-700">טופס</h2>
        <div className="mb-5">
          <label className="block mb-2 text-gray-600 font-medium">שם משתמש</label>
          <input
            type="text"
            name="field1"
            value={formData.field1}
            onChange={handleChange}
            className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-400 outline-none"
          />
        </div>
        <div className="mb-5">
          <label className="block mb-2 text-gray-600 font-medium">סיסמא</label>
          <input
            type="text"
            name="field2"
            value={formData.field2}
            onChange={handleChange}
            className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-400 outline-none"
          />
        </div>
        <button type="submit" className="w-full bg-blue-500 text-white p-3 rounded-lg hover:bg-blue-600 transition duration-200 shadow-md">שלח</button>
      </form>
    </div>
  );
};
