import { useState } from "react";
import { getMyBack } from "../serivces/userService";
import { getAllBackgrounds } from "../serivces/backgroundService";

export const MyBackgrounds =()=>{
        const [userBacks, setUserBacks] = useState([]);
    
        useEffect(() => {
            const fetchUserBacks = async () => {
               let id = JSON.parse(localStorage.getItem("user")).id
                const userBacks = await getMyBack(id);
                console.log(id + "--"+userBacks);
                try {
                    const allBackes = await getAllBackgrounds();
                    const filteredBacks = allBackes.filter(back => userBacks.includes(back.id));  // סינון הציטוטים השייכים למשתמש
                    setUserBacks(filteredBacks);
                } catch (err) {
                    console.error("Error fetching user quotes:", err);
                }
            };
            fetchUserBacks();
        }, []);
    
        const handleRemoveBack = async (id) => {
            try {
                await removeBack(id);  // קריאה לפונקציה להסרת הציטוט
                setUserQuotes(prevQuotes => prevQuotes.filter(quote => quote.Id !== id));  // עדכון המערך לאחר ההסרה
            } catch (err) {
                console.error("Failed to remove quote:", err);
            }
        };
    
        return (
            <div className="quote-list">
                {userQuotes.length > 0 ? (
                    userQuotes.map((quote) => (
                        <div className="sentence-item" key={quote.Id}>
                            <span
                                className="trash-button"
                                onClick={() => handleRemoveQuote(quote.Id)}
                                style={{ fontSize: '24px', cursor: 'pointer', color: 'red' }}
                            >
                                🗑️
                            </span>
                            <span
                                className="star-button2"
                                style={{ color: "gold", fontSize: "24px" }}
                            >
                                ★
                            </span>
                            <span className='priority'>{quote.priority}</span>
                            <div className="quote-text">
                                <span>{quote.text}</span>
                            </div>
                            <div className="quote-title">
                                <strong>{quote.title}</strong>
                            </div>
    
                        </div>
                    ))
                ) : (
                    <p>אין לך ציטוטים עדיין</p>
                )}
            </div>
        );
    
    
}
