import { useState, useEffect, createContext, useContext } from "react";

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(() => {
    const savedUser = localStorage.getItem("user");
    return savedUser ? JSON.parse(savedUser) : null;
  });

  useEffect(() => {
    user ? localStorage.setItem("user", JSON.stringify(user)) : localStorage.removeItem("user");
  }, [user]);

  const login = async (formData) => {
    try {
      const data = await userLogin(formData.name, formData.password);
      setUser({ id: data.id, name: data.name });
    } catch (error) {
      console.error("שגיאה: ", error);
    }
  };

  return (
    <AuthContext.Provider value={{ user, login }}>
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => useContext(AuthContext);

export const Login = () => {
  const { login } = useAuth();
  const [formData, setFormData] = useState({ name: "", password: "" });
  const [errors, setErrors] = useState({});

  const handleChange = (e) => setFormData({ ...formData, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    let newErrors = {};
    if (!formData.name) newErrors.name = "חובה להזין שם";
    if (!formData.password) newErrors.password = "חובה להזין סיסמה";
    setErrors(newErrors);
    
    if (Object.keys(newErrors).length === 0) await login(formData);
  };

  return (
    <div className="flex items-center justify-center min-h-screen bg-gray-200">
      <form onSubmit={handleSubmit} className="bg-white p-8 rounded-lg shadow-2xl w-96 border border-gray-300">
        <h2 className="text-3xl font-bold mb-6 text-center text-gray-700">התחברות</h2>
        <div className="mb-5">
          <label className="block mb-2 text-gray-600 font-medium">שם</label>
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleChange}
            className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-400 outline-none"
          />
          {errors.name && <p className="text-red-500 text-sm mt-1">{errors.name}</p>}
        </div>
        <div className="mb-5">
          <label className="block mb-2 text-gray-600 font-medium">סיסמה</label>
          <input
            type="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
            className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-400 outline-none"
          />
          {errors.password && <p className="text-red-500 text-sm mt-1">{errors.password}</p>}
        </div>
        <button type="submit" className="w-full bg-blue-500 text-white p-3 rounded-lg hover:bg-blue-600 transition duration-200 shadow-md">התחבר</button>
      </form>
    </div>
  );
};
