import axios from "axios";
const API_URL = 'http://localhost:3000/users';

export const userLogin = async (user) => {
    let result = "";
    try {
        const response = await axios.post(`${API_URL}/log`, { name: user.field1, pass: user.field2 });
        result = response.data;
    } catch (error) {
        result = error.message;
    }
    return result;
};
export const userRegister = async (user) => {
    let result = "";
    try {
        const response = await axios.post(`${API_URL}/`, user);
        result = response.data;
    } catch (error) {
        result = error.message;
    }
    return result;
};
export const getByIdUser = async (id) => {
    let result
    try {
        const response = await axios.get(`${API_URL}/${id}`);
        result = response.data;
        // result.myArts.map(art=>{
        // get for is arts.
        // });
    } catch (error) {
        result = error.message
    }
    return result;
}
export const getMyQuo = async (id) => {
    let result = "";
    try {
        const response = await getByIdUser(id)
        result = response.data.myArts;
    } catch (error) {
        result = error.message;
    }
    return result;
};
export const removeQuote = async (id) => {
    let result = "";
    try {
        const response = await axios.put(`${API_URL}/quote/d/${id}`)
        result = response.data.myArts;
    } catch (error) {
        result = error.message;
    }
    return result;
};
