import { useState } from "react";
import { userLogin } from "../serivces/userService";
import { useNavigate } from "react-router-dom";

export const Login = () => {
    const [formData, setFormData] = useState({ field1: "", field2: "" });
    const navigate = useNavigate();

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        let result = await userLogin(formData);
        let user = { id: result.id, name: result.name, mail: result.mail };
        localStorage.setItem("user", JSON.stringify(user));
        navigate('/home');
        window.location.reload();
    };

    return (
        <div className="d-flex align-items-center justify-content-center min-vh-100" style={{ backgroundImage: "url('https://source.unsplash.com/1600x900/?art')", backgroundSize: 'cover' }}>
            <div className="bg-light p-4 rounded shadow">
                <h2 className="text-center mb-4">התחבר</h2>
                <form onSubmit={handleSubmit}>
                    <div className="mb-3">
                        <label htmlFor="field1" className="form-label">שם משתמש</label>
                        <input
                            type="text"
                            name="field1"
                            value={formData.field1}
                            onChange={handleChange}
                            className="form-control"
                            placeholder="הכנס שם משתמש"
                            id="field1"
                        />
                    </div>
                    <div className="mb-3">
                        <label htmlFor="field2" className="form-label">סיסמה</label>
                        <input
                            type="password"
                            name="field2"
                            value={formData.field2}
                            onChange={handleChange}
                            className="form-control"
                            placeholder="••••••••"
                            id="field2"
                        />
                    </div>
                    <button type="submit" className="btn btn-primary w-100">התחבר</button>
                </form>
                <p className="mt-3 text-center">
                    אין לך חשבון? <a href="/register" className="link-primary">הרשם כאן</a>
                </p>
            </div>
        </div>
    );
};
