import { useState } from "react";
import { useNavigate } from "react-router-dom";
import { userRegister } from "../serivces/userService";

export const Register = () => {
    const [formData, setFormData] = useState({ name: "", mail: "", password: "" });
    const navigate = useNavigate();

    const handleChange = (e) => setFormData({ ...formData, [e.target.name]: e.target.value });

    const handleSubmit = async (e) => {
        e.preventDefault();
        let result = await userRegister(formData);
        let user = { id: result.id, name: result.name, mail: result.mail };
        localStorage.setItem("user", JSON.stringify(user));
        window.dispatchEvent(new Event("storage"));
        navigate('/home');
        window.location.reload();
    };

    return (
        <div class>
            <div className="d-flex align-items-center justify-content-center vh-100" style={{ backgroundColor: '#f8f9fa' }}>
                <form onSubmit={handleSubmit} className="bg-white p-4 rounded shadow w-100" style={{ maxWidth: '400px' }}>
                    <h2 className="text-center mb-4">הרשמה</h2>

                    <div className="mb-3">
                        <label htmlFor="name" className="form-label">שם</label>
                        <input
                            type="text"
                            name="name"
                            id="name"
                            value={formData.name}
                            onChange={handleChange}
                            className="form-control"
                            placeholder="הכנס שם"
                        />
                    </div>

                    <div className="mb-3">
                        <label htmlFor="mail" className="form-label">אימייל</label>
                        <input
                            type="email"
                            name="mail"
                            id="mail"
                            value={formData.mail}
                            onChange={handleChange}
                            className="form-control"
                            placeholder="הכנס אימייל"
                        />
                    </div>

                    <div className="mb-3">
                        <label htmlFor="password" className="form-label">סיסמה</label>
                        <input
                            type="password"
                            name="password"
                            id="password"
                            value={formData.password}
                            onChange={handleChange}
                            className="form-control"
                            placeholder="••••••••"
                        />
                    </div>

                    <button type="submit" className="btn btn-primary w-100">
                        הרשמה
                    </button>
                </form>
            </div>
        </div>
    );
};
