import { useEffect, useState } from "react";
import { getAllQuotes, putPriority } from "../serivces/quoteService";
import { addQuote, getMyQuo, removeQuote } from "../serivces/userService";

export const Quotes = () => {
    const [quotes, setQuotes] = useState([]);
    const [favorites, setFavorites] = useState([]);
    const userId = JSON.parse(localStorage.getItem("user")).id

    useEffect(() => {
        const show = async () => {
            try {
                let result = await getAllQuotes();
                setQuotes(result);
                console.log(userId);
                   setFavorites(await getMyQuo(userId)); // ודא שאתה מעביר את ה-ID הנכון
                
            }
            catch (e) {
            console.error(e);
        }
    };
    show();
}, []);

const handleFavoriteClick = async (id) => {
    try {
        if (favorites.includes(id)) {
            await removeQuote(userId,id);  // פונקציה להסרת ציטוט מהמועדפים
        } else {
            // אם ה-id לא קיים במערך, נוסיף אותו
            await addQuote(userId,id); // פונקציה להוספת ציטוט למועדפים
        }
        setFavorites(await getMyQuo(userId));
    } catch (err) {
        console.error("Failed to update favorite:", err);
    }
};

const addPri = (id) => {
    const addPriority = async (id) => {
        let newItem = await putPriority(id);
        setQuotes(quotes.map(item =>
            item.id === id ? newItem : item
        ));
    };
    addPriority(id);
};

return (
    // <div style={{ display: "table", width: "100%" }}>
    //     {quotes.map((quote, index) => (
    //         <div key={index} style={{ display: "table-row", width: "100%" }}>
    //             <div style={{ display: "table-cell", padding: "10px", borderBottom: "1px solid #ccc", textAlign: "right", display: "flex", alignItems: "center", gap: "2px" }}>
    //                 <button 
    //                     onClick={() => addPri(quote.id)} 
    //                     style={{
    //                         background: "none", 
    //                         border: "none", 
    //                         fontSize: "20px", 
    //                         cursor: "pointer", 
    //                         transition: "color 0.3s",
    //                         color: quote.priority > 0 ? "red" : "black",
    //                         padding: 0,
    //                         margin: 1
    //                     }}
    //                 >
    //                     {quote.priority > 0 ? "⭐" : "⭐"}
    //                 </button>
    //                 <span>{quote.priority}</span>
    //             </div>
    //             <div style={{ display: "table-cell", padding: "10px", borderBottom: "1px solid #ccc", textAlign: "right" }}>{quote.text}</div>
    //             <div style={{ display: "table-cell", padding: "10px", borderBottom: "1px solid #ccc", textAlign: "right", fontWeight: "bold" }}>{quote.title}</div>
    //         </div>
    //     ))}
    // </div>

    <div className="quote-list">
        {Array.isArray(quotes) && quotes.length > 0 ? (
            quotes.map((sentence, index) => (
                <div className="sentence-item" key={index}>
                    <span
                        className="heart-button"
                        onClick={() => handleFavoriteClick(sentence.id)}
                        style={{ color: favorites.includes(sentence.id) ? 'red' : 'gray', fontSize: "24px" }}
                    >like!
                    </span>
                    <span
                        className="star-button2"
                        onClick={() => addPri(sentence.id)}
                        style={{ color: "gold", fontSize: "24px" }}
                    >
                        ★
                    </span>
                    <span className='priority'>{sentence.priority}</span>
                    <span className='quote-text'>{sentence.text}</span>
                    <strong className='quote-title'>{sentence.title}</strong>
                </div>
            ))
        ) : (
            <p>אין משפטים</p>
        )}
    </div>
);
};
