import { useEffect, useState } from "react";
import { getAllBackgrounds } from "../serivces/backgroundService";
import { addBack, getMyBack, removeBack } from "../serivces/userService";

export const Backgrounds = () => {
    const [images, setImages] = useState([]);
    const [loadError, setLoadError] = useState(false); // ניהול שגיאות טעינה
    const [favorites, setFavorites] = useState([]);
    const userId = JSON.parse(localStorage.getItem("user")).id

    useEffect(() => {
        const fetchImages = async () => {
            try {
                const data = await getAllBackgrounds();
                setImages(data);
                setFavorites(await getMyBack(userId));
            } catch (error) {
                console.error("Error fetching images:", error);
                setLoadError(true);
            }
        };

        fetchImages();
    }, []);
    const handleFavoriteClick = async (id) => {
        
        try {
            if (favorites.includes(id)) {
                await removeBack(userId, id);  // פונקציה להסרת ציטוט מהמועדפים
                console.log("1");
            } else {
                // אם ה-id לא קיים במערך, נוסיף אותו
                await addBack(userId, id); // פונקציה להוספת ציטוט למועדפים

            }
            setFavorites(await getMyQuo(userId));            
        } catch (err) {
            console.error("Failed to update favorite:", err);
        }
    };


    const convertDriveUrl = (originalUrl) => {
        const fileIdMatch = originalUrl.match(/[-\w]{25,}/);
        return fileIdMatch ? `https://drive.google.com/thumbnail?id=${fileIdMatch[0]}&sz=w1000` : originalUrl;
    };

    return (
        <div>
            {loadError ? (
                <p>Error loading images.</p>
            ) : images.length > 0 ? (
                images.map((img, index) => (
                    <>
                        <span
                            className="heart-button"
                            onClick={() => handleFavoriteClick(sentence.id)}
                            style={{ color: favorites.includes(sentence.id) ? 'red' : 'gray', fontSize: "24px" }}
                        >like!
                        </span>
                        <img
                            key={index}
                            src={convertDriveUrl(img.url)}
                            alt={`Image ${index}`}
                            width="200"
                            onError={(e) => {
                                console.error("Image failed to load:", e.target.src);
                                e.target.src = "path/to/placeholder/image.jpg"; // תמונה חלופית
                            }}
                        />
                    </>
                ))
            ) : (
                <p>Loading images...</p>
            )}
        </div>
    );
};
