import { useEffect, useState } from "react";
import { getAllBackgrounds } from "../serivces/backgroundService";
import { getMyBack, removeBack } from "../serivces/userService";



export const Backgrounds = () => {
    const [images, setImages] = useState([]);
    const [favorites, setFavorites] = useState([]);
    const [imageLoadErrors, setImageLoadErrors] = useState(new Set());
    const userId = JSON.parse(localStorage.getItem("user")).id;


    useEffect(() => {
        const fetchImages = async () => {
            try {
                const data = await getAllBackgrounds();
                const validImage = data.map(img => ({ ...img, url: convertDriveUrl(img.url) }));

                setImages(prevImage => {
                    const isSame = JSON.stringify(prevImage) === JSON.stringify(validImage);
                    return isSame ? prevImage : validImage;
                });
            } catch (error) {
                console.error("Error fetching images:", error);
                // טיפול בשגיאה - לדוגמה, ניתן להחזיר ערך ברירת מחדל או להציג הודעת שגיאה למשתמש
            }
        };
        const fetchFavorites = async () => {
            try {
                const myBackgrounds = await getMyBack(); // קבלת רשימת ה-IDs של המועדפים
                setFavorites(myBackgrounds); // שמירה במצב
            } catch (err) {
                console.error("Error fetching favorites:", err);
            }
        };
        fetchImages();
        fetchFavorites();
    }, []);


    const convertDriveUrl = (originalUrl) => {
        if (!originalUrl) return '/397A9871 העתק.jpg';
        const fileIdMatch = originalUrl.match(/[-\w]{25,}/);
        return fileIdMatch ? `https://drive.google.com/thumbnail?id=${fileIdMatch[0]}&sz=w1000` : originalUrl
    };
    const like = async (id) => {
        try {
            if (favorites.includes(id)) {
                await removeBack(id);  // מסיר מהשרת
            } else {
                await addBackground(id);  // מוסיף לשרת
            }

            // שליפת רשימת המועדפים מחדש מהשרת כדי להבטיח עקביות
            const updatedFavorites = await getMyBackground();
            setFavorites(updatedFavorites);
        } catch (err) {
            console.error("Failed to update favorite:", err);
        }
    };
    // useEffect(() => {
    //     const imgShowing = async () => {
    //         try {
    //             const data = await getAllBackgrounds();
    //             const validImages = data.map(img => ({
    //                 ...img,
    //                 url: convertDriveUrl(img.url),
    //             }));
    //             setImages(validImages);
    //         } catch (error) {
    //             console.error("Failed to fetch backgrounds:", error);
    //         }
    //     };

    //     imgShowing();
    // }, []);

    // const handleFavoriteClick = async (id) => {
    //     try {
    //         if (favorites.includes(id)) {
    //             await removeBack(userId, id);
    //         } else {
    //             await addBack(userId, id);
    //         }
    //         const userFavorites = await getMyBack(userId);
    //         setFavorites(userFavorites);
    //     } catch (err) {
    //         console.error("Failed to update favorite:", err);
    //     }
    // };

    // const convertDriveUrl = (originalUrl) => {
    //     if (!originalUrl)             return "/d.jpg"

    //     const fileIdMatch = originalUrl.match(/[-\w]{25,}/);
    //     if (!fileIdMatch) {
    //         console.warn("Invalid URL:", originalUrl);
    //         return "/d.jpg"
    //     }
    //     const convertedUrl = `https://drive.google.com/uc?export=view&id=${fileIdMatch[0]}`;
    //     console.log("Original URL:", originalUrl);
    //     console.log("Converted URL:", convertedUrl);
    //     return convertedUrl;
    // };


    // const like = async (id) => {
    //     try {
    //         if (favorites.includes(id)) {
    //             await removeBack(id);
    //         } else {
    //             await addBack(id);
    //         }
    //         const updatedFavorites = await getMyBack();
    //         setFavorites(updatedFavorites);
    //     } catch (error) {
    //         console.error("Failed to update favorite:", error);
    //     }
    // };

    return (
        <div>
            {images.length > 0 ? (
                images.map((img, index) => (
                    <div key={img.id} className="image-wrapper">
                        <img
                            src={img.url}
                            alt={`Image ${index}`}
                            width="200"
                            onError={(e) => {
                                if (!imageLoadErrors.has(img.url)) {
                                    setImageLoadErrors(prev => new Set(prev).add(img.url));
                                    e.target.src = "/d.jpg";
                                    console.error("Image failed to load:", e.target.src);
                                }
                            }}
                        />
                        <div className="button-container">
                            <button className="heart-button" onClick={() => like(img.id)}>like!</button>
                        </div>
                    </div>
                ))
            ) : (
                <p>Loading images...</p>
            )}
        </div>
    );
};
