import { useEffect, useState } from "react"
import { Link } from "react-router-dom"

export const Nav = () => {
    const [name, setName] = useState("")
    useEffect(() => {
        const updateUser = () => {
            let user = JSON.parse(localStorage.getItem("user"));
            setName(user ? user.name : "");
        };
        updateUser(); // הפעלה ראשונית
        window.addEventListener("storage", updateUser); // האזנה לשינויים
        return () => window.removeEventListener("storage", updateUser); // ניקוי מאזין
    }, []);
    const logout = () => {
        localStorage.removeItem("user");
        window.location.reload();
    }
    return  (
        <nav className="navbar navbar-expand-lg navbar-dark bg-dark shadow">
            <div className="container">
                {/* כפתור תפריט למובייל */}
                <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav">
                    <span className="navbar-toggler-icon"></span>
                </button>

                {/* תוכן התפריט */}
                <div className="collapse navbar-collapse" id="navbarNav">
                    <ul className="navbar-nav me-auto">
                        <li className="nav-item">
                            <Link to="/home" className="nav-link">Home</Link>
                        </li>
                        <li className="nav-item">
                            <Link to="/fam" className="nav-link">Famous Arts</Link>
                        </li>
                        {name && <>
                            <li className="nav-item"><Link to="/myArts" className="nav-link">My Arts</Link></li>
                            <li className="nav-item"><Link to="/mySug" className="nav-link">My Suggestions</Link></li>
                            <li className="nav-item"><Link to="/newArt" className="nav-link">New Art</Link></li>
                            <li className="nav-item"><Link to="/quotes" className="nav-link">Quotes</Link></li>
                            <li className="nav-item"><Link to="/back" className="nav-link">Backgrounds</Link></li>
                        </>}
                    </ul>

                    {/* תפריט משתמש */}
                    <ul className="navbar-nav">
                        {name ? (
                            <>
                                <li className="nav-item">
                                    <Link to="/pro" className="nav-link fw-bold">{name}</Link>
                                </li>
                                <li className="nav-item">
                                    <button onClick={logout} className="btn btn-danger ms-2">Logout</button>
                                </li>
                            </>
                        ) : (
                            <>
                                <li className="nav-item">
                                    <Link to="/log" className="nav-link">Login</Link>
                                </li>
                                <li className="nav-item">
                                    <Link to="/reg" className="nav-link">Register</Link>
                                </li>
                            </>
                        )}
                    </ul>
                </div>
            </div>
        </nav>)}
