import { useEffect, useState } from "react";
import { getAllQuotes, putPriority } from "../serivces/quoteService";

export const Quotes = () => {
    const [quotes, setQuotes] = useState([]);
    const [favorites, setFavorites] = useState([]);
    
    useEffect(() => {
        const show = async () => {
            try{
            let result = await getAllQuotes();
            setQuotes(result);
            }
            catch(e){
                console.error(e)
            }
        };
        show();
    }, []);

    const handlePriorityClick = async (id) => {
        try {
            await putPriority(id);
            setSentences(prevSentences =>
                prevSentences.map(sentence =>
                    sentence.Id === id
                        ? { ...sentence, priority: sentence.priority + 1 }
                        : sentence
                )
            );
        } catch (err) {
            console.error("Failed to update priority:", err);
        }
    };

    const handleFavoriteClick = async (id) => {
      try {
          // אם ה-id כבר קיים במערך, נבצע הסרה
          if (favorites.includes(id)) {
              await removeQuote(id);  // פונקציה להסרת ציטוט מהמועדפים
              setFavorites((prevFavorites) =>
                  prevFavorites.filter(favId => favId !== id) // הסרה
              );
          } else {
              // אם ה-id לא קיים במערך, נוסיף אותו
              await addQuote(id); // פונקציה להוספת ציטוט למועדפים
              setFavorites((prevFavorites) =>
                  [...prevFavorites, id] // הוספה
              );
          }
      } catch (err) {
          console.error("Failed to update favorite:", err);
      }
  };

    const addPri = (id) => {
        const addPriority = async (id) => {
            let newItem = await putPriority(id);
            setQuotes(quotes.map(item =>
                item.id === id ? newItem : item
            ));
        };
        addPriority(id);
    };

    return (
        <div style={{ display: "table", width: "100%" }}>
            {quotes.map((quote, index) => (
                <div key={index} style={{ display: "table-row", width: "100%" }}>
                    <div style={{ display: "table-cell", padding: "10px", borderBottom: "1px solid #ccc", textAlign: "right", display: "flex", alignItems: "center", gap: "2px" }}>
                        <button 
                            onClick={() => addPri(quote.id)} 
                            style={{
                                background: "none", 
                                border: "none", 
                                fontSize: "20px", 
                                cursor: "pointer", 
                                transition: "color 0.3s",
                                color: quote.priority > 0 ? "red" : "black",
                                padding: 0,
                                margin: 1
                            }}
                        >
                            {quote.priority > 0 ? "⭐" : "⭐"}
                        </button>
                        <span>{quote.priority}</span>
                    </div>
                    <div style={{ display: "table-cell", padding: "10px", borderBottom: "1px solid #ccc", textAlign: "right" }}>{quote.text}</div>
                    <div style={{ display: "table-cell", padding: "10px", borderBottom: "1px solid #ccc", textAlign: "right", fontWeight: "bold" }}>{quote.title}</div>
                </div>
            ))}
        </div>
    );
};
