import { useEffect, useState } from "react";
import { getAllQuotes, putPriority } from "../serivces/quoteService";

export const Quotes = () => {
    const [quotes, setQuotes] = useState([]);
    
    useEffect(() => {
        const show = async () => {
            let result = await getAllQuotes();
            setQuotes(result);
        };
        show();
    }, []);
    
    const addPri = (id) => {
        const addPriority = async (id) => {
            let newItem = await putPriority(id);
            setQuotes(quotes.map(item =>
                item.id === id ? newItem : item
            ));
        };
        addPriority(id);
    };

    return (
        <div style={{ display: "table", width: "100%" }}>
            {quotes.map((quote, index) => (
                <div key={index} style={{ display: "table-row", width: "100%" }}>
                    <div style={{ display: "table-cell", padding: "10px", borderBottom: "1px solid #ccc", textAlign: "right", display: "flex", alignItems: "center", gap: "2px" }}>
                        <span>{quote.priority}</span>
                        <button 
                            onClick={() => addPri(quote.id)} 
                            style={{
                                background: "none", 
                                border: "none", 
                                fontSize: "24px", 
                                cursor: "pointer", 
                                transition: "color 0.3s",
                                color: quote.priority > 0 ? "red" : "black",
                                padding: 0,
                                margin: 0
                            }}
                        >
                            {quote.priority > 0 ? "‚ù§Ô∏è" : "ü§ç"}
                        </button>
                    </div>
                    <div style={{ display: "table-cell", padding: "10px", borderBottom: "1px solid #ccc", textAlign: "right", fontWeight: "bold" }}>{quote.title}</div>
                    <div style={{ display: "table-cell", padding: "10px", borderBottom: "1px solid #ccc", textAlign: "right" }}>{quote.text}</div>
                    
                </div>
            ))}
        </div>
    );
};
