    //         <nav className="navbar navbar-expand-lg navbar-dark bg-dark shadow">
    //             <div className="container d-flex justify-content-between align-items-center">
    //                 <ul className="navbar-nav flex-row d-none d-lg-flex">
    //                     {name && <li className="nav-item mx-2"><Link className="nav-link" to={'pro'}>{name}</Link></li>}
    //                     {!name && <li className="nav-item mx-2"><Link className="nav-link" to={'log'}>Login</Link></li>}
    //                     {!name && <li className="nav-item mx-2"><Link className="nav-link" to={'reg'}>Register</Link></li>}
    //                     {name && (<li className="nav-item mx-2"><button className="btn btn-outline-light btn-sm" onClick={logout}>Logout</button></li>)}
    //                 </ul>

    //                 <Link to={'home'} className="navbar-brand mx-auto d-flex align-items-center">
    //                     <img
    //                         src="/logo.jpg"
    //                         alt="Logo"
    //                         style={{
    //                             height: "80px",
    //                             width: "80px",
    //                             borderRadius: "50%",
    //                             border: "2px solid white",
    //                             boxShadow: "0 0 10px rgba(255,255,255,0.7)"
    //                         }}
    //                     />
    //                 </Link>

    //                 <ul className="navbar-nav flex-row d-none d-lg-flex">
    //                     {name && <li className="nav-item mx-2"><Link className="nav-link" to={'mySug'}>My Suggestions</Link></li>}
    //                     {name && <li className="nav-item mx-2"><Link className="nav-link" to={'myQu'}>My Quotes</Link></li>}
    //                     {name && <li className="nav-item mx-2"><Link className="nav-link" to={'myBack'}>My Backgrounds</Link></li>}
    //                     {name && <li className="nav-item mx-2"><Link className="nav-link" to={'newArt'}>New Art</Link></li>}
    //                     <li className="nav-item mx-2"><Link className="nav-link" to={'home'}>Home Page</Link></li>
    //                     {name && <li className="nav-item mx-2"><Link className="nav-link" to={'quotes'}>Quotes</Link></li>}
    //                     {name && <li className="nav-item mx-2"><Link className="nav-link" to={'back'}>Backgrounds</Link></li>}
    //                 </ul>
    //             </div>
    //         </nav>
    //         <div><img src="/logo.jpg" alt="Logo" style={{ height: "200px", borderRadius: "50%" }} /></div>

    //     </header>
    <header>
            {/* לוגו מעל ה-Navbar */}
            <div className="text-center my-3">
                <Link to={'home'}>
                    <img 
                        src="/logo.jpg" 
                        alt="Logo" 
                        style={{
                            height: "100px",
                            width: "100px",
                            borderRadius: "50%",
                            border: "2px solid #fff",
                            boxShadow: "0 0 10px rgba(0,0,0,0.3)"
                        }} 
                    />
                </Link>
            </div>

            {/* Navbar קבוע */}
            <nav className="navbar navbar-expand-lg navbar-dark bg-dark shadow">
                <div className="container d-flex justify-content-between align-items-center">

                    {/* צד שמאל */}
                    <ul className="navbar-nav flex-row">
                        {name && <li className="nav-item mx-2"><Link className="nav-link" to={'mySug'}>My Suggestions</Link></li>}
                        {name && <li className="nav-item mx-2"><Link className="nav-link" to={'myQu'}>My Quotes</Link></li>}
                        {name && <li className="nav-item mx-2"><Link className="nav-link" to={'myBack'}>My Backgrounds</Link></li>}
                        {name && <li className="nav-item mx-2"><Link className="nav-link" to={'newArt'}>New Art</Link></li>}
                        <li className="nav-item mx-2"><Link className="nav-link" to={'home'}>Home Page</Link></li>
                        {name && <li className="nav-item mx-2"><Link className="nav-link" to={'quotes'}>Quotes</Link></li>}
                        {name && <li className="nav-item mx-2"><Link className="nav-link" to={'back'}>Backgrounds</Link></li>}
                    </ul>

                    {/* צד ימין */}
                    <ul className="navbar-nav flex-row">
                        {name && <li className="nav-item mx-2"><Link className="nav-link" to={'pro'}>{name}</Link></li>}
                        {!name && <li className="nav-item mx-2"><Link className="nav-link" to={'log'}>Login</Link></li>}
                        {!name && <li className="nav-item mx-2"><Link className="nav-link" to={'reg'}>Register</Link></li>}
                        {name && (<li className="nav-item mx-2"><button className="btn btn-outline-light btn-sm" onClick={logout}>Logout</button></li>)}
                    </ul>

                </div>
            </nav>
        </header>
    );
}
