import { useEffect, useState } from "react";
import { getAllQuotes, putPriority } from "../serivces/quoteService";

export const Quotes = () => {
    const [quotes, setQuotes] = useState([]);
    
    useEffect(() => {
        const show = async () => {
            let result = await getAllQuotes();
            setQuotes(result);
        };
        show();
    }, []);
    
    const addPri = (id) => {
        const addPriority = async (id) => {
            let newItem = await putPriority(id);
            setQuotes(quotes.map(item =>
                item.id === id ? newItem : item
            ));
        };
        addPriority(id);
    };

    return (
        <div>
            {quotes.map((quote, index) => (
                <div key={index} style={{ border: "1px solid #ccc", padding: "10px", marginBottom: "10px", borderRadius: "8px", textAlign: "right" }}>
                    <h2>{quote.title}</h2>
                    <p>{quote.text}</p>
                    <div style={{ display: "flex", flexDirection: "row-reverse", alignItems: "center" }}>
                        <button 
                            onClick={() => addPri(quote.id)} 
                            style={{
                                background: "none", 
                                border: "none", 
                                fontSize: "24px", 
                                cursor: "pointer", 
                                transition: "color 0.3s",
                                color: quote.priority > 0 ? "red" : "black",
                                lineHeight: "24px",
                                marginLeft: "10px"
                            }}
                        >
                            {quote.priority > 0 ? "‚ù§Ô∏è" : "ü§ç"}
                        </button>
                        <p style={{ lineHeight: "24px" }}>{quote.priority}</p>
                    </div>
                </div>
            ))}
        </div>
    );
};
